mapper.select:
SELECT B.board_id, B.account, U.nickname,
B.category_id, B.content, B.view_count, B.created_at, B.updated_at FROM tbl_board B JOIN tbl_user U ON B.account = U.account
WHERE B.status = 'A' AND B.category_id = 2 AND B.board_id = #{boardId}

mapper.update:
UPDATE tbl_board
SET content = #{content}
WHERE status = 'A'
    AND board_id = #{boardId}
    AND account = #{account}

UPDATE tbl_board
SET view_count = view_count + 1
WHERE board_id = #{boardId}

mapper.select:
SELECT view_count
FROM tbl_board
WHERE board_id = #{boardId}
